(210,	83,	'_activities_0_activity_title',	'field_61597b5b5cae5'),
(211,	83,	'activities_0_activity_description',	'<ul>\r\n 	<li>Supporting the review and recommendation of key standards especially those relevant to HIV/AIDS and TB care</li>\r\n 	<li>Constituting relevant stakeholders to validate and endorse health information standards for use.</li>\r\n 	<li>Support the building and implementation of relevant HL7 FHIR resources</li>\r\n 	<li>Recommending terminology standards including aggregate data exchange (ADX) formats for use by countries</li>\r\n 	<li>Providing guidance on national level metadata management</li>\r\n 	<li>Providing supporting dissemination of materials related to standards.\"</li>\r\n</ul>'),
(212,	83,	'_activities_0_activity_description',	'field_615af203c6b87'),
(213,	83,	'activities_1_activity_title',	'Interoperability:'),
(214,	83,	'_activities_1_activity_title',	'field_61597b5b5cae5'),
(215,	83,	'activities_1_activity_description',	'<ol>\r\n 	<li>Support countries to come up with national health information architectures</li>\r\n 	<li>Allow sharing of knowledge of health information exchange domains, exchange formats, and interoperability serviced layers</li>\r\n 	<li>Support organizational, semantic, syntactic and technical interoperability issues for countries</li>\r\n 	<li>Explore practical interoperability use-cases (such as Case-based surveillance) to help countries actualize functional HIE.</li>\r\n 	<li>Aggregate, organize and share existing national and interoperability architectures, related toolkits and user guides.</li>\r\n</ol>'),
